
public class Wap_to_pa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=6;i++) {
			for(int j=1;j<=6;j++) {
				
				if(i%2==0) {
					System.out.print("01");
				}
				else {
					System.out.print("10");
				}
				j++;
			}
			System.out.println();
		
			
			
		}

	}

}
